import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';

const TERMS = [
  {
    key: 'terms1',
    label: '노바문화센터 이용약관 동의',
    required: true,
    content: '여러분을 환영합니다. 노바문화센터 서비스 및 제품(이하 “서비스”)을 이용해 주셔서 감사합니다. 본 약관은 다양한 노바문화센터 서비스의 이용과 관련하여 노바문화센터 서비스를 제공하는 노바문화센터 주식회사(이하 “노바문화센터”)와 이를 이용하는 “회원”과의 관계, 회원의 서비스 이용 절차, 권리·의무 및 책임사항 등을 규정합니다.'
  },
  {
    key: 'terms2',
    label: '개인정보 수집 및 이용 동의',
    required: true,
    content: '개인정보보호법에 따라 회원가입 신청하시는 분께 수집하는 개인정보의 항목, 수집 및 이용목적, 보유 및 이용기간, 동의 거부권 및 동의 거부 시 불이익에 관한 사항을 안내 드립니다.'
  },
  {
    key: 'terms3',
    label: '프로모션 정보 수신 동의',
    required: false,
    content: '노바문화센터에서 제공하는 이벤트/혜택 등 다양한 정보를 휴대전화(앱 알림 또는 문자), 이메일로 받아보실 수 있습니다. 일부 서비스(별도 회원 체계로 운영하거나 노바문화센터 가입 이후 추가 가입하여 이용하는 서비스 등)의 경우, 별도의 동의 후 서비스 이용이 가능합니다.'
  }
];

function SignupPage() {
  // 회원가입 단계 관리: 'terms' -> 'verification' -> 'signup'
  const [currentStep, setCurrentStep] = useState('terms');

  // 약관 동의 처리
  const [checked, setChecked] = useState({ terms1: false, terms2: false, terms3: false });
  const allRequiredChecked = TERMS.filter(t => t.required).every(t => checked[t.key]);
  const allChecked = TERMS.every(t => checked[t.key]);

  const handleAllTerms = e => setChecked({ terms1: e.target.checked, terms2: e.target.checked, terms3: e.target.checked });
  const handleOneTerm = key => e => setChecked(prev => ({ ...prev, [key]: e.target.checked }));

  // 본인인증 상태
  const [verificationMethod, setVerificationMethod] = useState(null); // 'phone' | 'email' | null
  const [isPhoneVerified, setIsPhoneVerified] = useState(false);
  const [isEmailVerified, setIsEmailVerified] = useState(false);
  const isAnyMethodVerified = isPhoneVerified || isEmailVerified;

  // 인증코드 관련 상태
  const [phoneCode, setPhoneCode] = useState('');
  const [sentPhoneCode, setSentPhoneCode] = useState('');
  const [emailCode, setEmailCode] = useState('');
  const [sentEmailCode, setSentEmailCode] = useState('');

  // 회원가입 폼 상태 및 아이디 중복검사
  const [form, setForm] = useState({ userId: '', password: '', passwordConfirm: '', phone: '', email: '' });
  const [idCheckResult, setIdCheckResult] = useState(null); // null | 'OK' | 'DUPLICATE' | 'ERROR'
  const [errors, setErrors] = useState({});

  useEffect(() => {
    // 본인인증 완료 시 회원가입 단계로 전환
    if (isAnyMethodVerified && currentStep === 'verification') {
      setCurrentStep('signup');
    }
  }, [isAnyMethodVerified, currentStep]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setErrors(prev => ({ ...prev, [name]: undefined }));
    if (name === 'userId') setIdCheckResult(null);
  };

  // 아이디 중복확인
  const checkDuplicateId = async () => {
    if (!form.userId) {
      setErrors(e => ({ ...e, userId: '아이디를 입력해주세요.' }));
      return;
    }
    try {
      // 실제 API 호출 시에는 form.userId를 서버로 전송
      const res = await axios.get(`/api/users/check-id?userId=${encodeURIComponent(form.userId)}`);
      if (res.data.available) { // 예시: { available: true }
        setIdCheckResult('OK');
      } else { // 예시: { available: false }
        setIdCheckResult('DUPLICATE');
      }
    } catch (err) {
      console.error("아이디 중복 확인 에러:", err);
      setIdCheckResult('ERROR');
    }
  };

  // 휴대폰 인증번호 발송
  const handleSendPhoneCode = () => {
    if (!form.phone) {
      setErrors(e => ({ ...e, phone: '휴대폰 번호를 입력하세요.' }));
      return;
    }
    // 실제 서비스에서는 API로 인증번호 발송
    setSentPhoneCode('123456'); // 예시 인증번호
    setErrors(e => ({ ...e, phone: undefined }));
    alert('인증번호가 발송되었습니다. (예시: 123456)');
  };

  // 휴대폰 인증번호 확인
  const handleVerifyPhone = () => {
    if (phoneCode === sentPhoneCode && phoneCode) {
      setIsPhoneVerified(true);
      setErrors(e => ({ ...e, phone: undefined }));
      alert('휴대폰 인증이 완료되었습니다.');
    } else {
      setIsPhoneVerified(false);
      setErrors(e => ({ ...e, phone: '인증번호가 일치하지 않습니다.' }));
    }
  };

  // 이메일 인증번호 발송
  const handleSendEmailCode = () => {
    if (!form.email) {
      setErrors(e => ({ ...e, email: '이메일을 입력하세요.' }));
      return;
    }
    // 실제 서비스에서는 API로 인증번호 발송
    setSentEmailCode('654321'); // 예시 인증번호
    setErrors(e => ({ ...e, email: undefined }));
    alert('인증번호가 발송되었습니다. (예시: 654321)');
  };

  // 이메일 인증번호 확인
  const handleVerifyEmail = () => {
    if (emailCode === sentEmailCode && emailCode) {
      setIsEmailVerified(true);
      setErrors(e => ({ ...e, email: undefined }));
      alert('이메일 인증이 완료되었습니다.');
    } else {
      setIsEmailVerified(false);
      setErrors(e => ({ ...e, email: '인증번호가 일치하지 않습니다.' }));
    }
  };

  // 최종 회원가입 폼 필수값 검증
  const validateSignupForm = () => {
    const newErrors = {};
    if (!form.userId) newErrors.userId = '아이디를 입력하세요.';
    if (idCheckResult !== 'OK') newErrors.userId = '아이디 중복확인을 해주세요.';
    if (!form.password) newErrors.password = '비밀번호를 입력하세요.';
    if (!form.passwordConfirm) newErrors.passwordConfirm = '비밀번호 확인을 입력하세요.';
    if (form.password !== form.passwordConfirm) newErrors.passwordConfirm = '비밀번호가 일치하지 않습니다.';
    
    // 본인인증은 이미 완료된 상태에서 넘어오므로, 여기서 다시 검증할 필요는 없음.
    // 다만, 폼 데이터에 휴대폰/이메일 정보가 없는 경우를 대비하여 추가할 수 있음.
    if (!form.phone && !form.email) newErrors.general = '휴대폰 또는 이메일 정보가 필요합니다.'; // 이 경우는 본인인증 단계에서 걸러질 것임.

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmitSignup = e => {
    e.preventDefault();
    if (!validateSignupForm()) {
      alert('필수 입력 정보를 확인해주세요.');
      return;
    }
    // TODO: 실제 회원가입 API 호출
    console.log('회원가입 데이터:', form);
    alert('회원가입이 완료되었습니다!');
    // 회원가입 성공 후 페이지 이동 로직 추가 (예: 로그인 페이지)
  };

  return (
    <div className="page-container auth-page">
      <h2>회원가입</h2>

      {currentStep === 'terms' && (
        <div className="terms-box">
          <div className="terms-content">
            <label className="terms-all-label">
              <input type="checkbox" checked={allChecked} onChange={handleAllTerms} />
              <span className="terms-all-text"><b>노바문화센터 이용약관, 개인정보 수집 및 이용, 프로모션 정보 수신(선택)에 모두 동의합니다.</b></span>
            </label>
            <div className="terms-list">
              {TERMS.map(term => (
                <div className="terms-item" key={term.key}>
                  <label className="terms-item-label">
                    <input type="checkbox" checked={checked[term.key]} onChange={handleOneTerm(term.key)} />
                    <span className="terms-item-title">{term.label} {term.required ? <span className="terms-required">(필수)</span> : <span className="terms-optional">(선택)</span>}</span>
                  </label>
                  <div className="terms-scrollbox">{term.content}</div>
                </div>
              ))}
            </div>
            <button
              className="terms-next-btn"
              onClick={() => setCurrentStep('verification')}
              disabled={!allRequiredChecked}
            >
              확인
            </button>
          </div>
        </div>
      )}

      {currentStep === 'verification' && (
        <div className="verification-section">
          <h3>본인인증</h3>
          <p>회원가입을 위해 휴대폰 또는 이메일 중 한 가지 방법으로 본인인증을 진행해주세요.</p>

          {/* 휴대폰 인증 섹션 */}
          <div className="verification-method-box">
            <h4>휴대폰 인증</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
              <input
                type="tel"
                name="phone"
                placeholder="휴대폰 번호"
                value={form.phone}
                onChange={handleChange}
                disabled={isPhoneVerified || verificationMethod === 'email'} // 다른 인증 방식 선택 시 비활성화
              />
              <button
                type="button"
                onClick={() => { setVerificationMethod('phone'); handleSendPhoneCode(); }}
                disabled={isPhoneVerified || verificationMethod === 'email'}
              >
                인증번호 발송
              </button>
            </div>
            {verificationMethod === 'phone' && sentPhoneCode && !isPhoneVerified && (
              <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginTop: 4 }}>
                <input
                  type="text"
                  placeholder="인증번호 입력"
                  value={phoneCode}
                  onChange={e => setPhoneCode(e.target.value)}
                  style={{ flex: 1 }}
                />
                <button type="button" onClick={handleVerifyPhone}>인증확인</button>
              </div>
            )}
            {isPhoneVerified && <span style={{ color: 'green', fontSize: 13 }}>휴대폰 인증 완료</span>}
            {errors.phone && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.phone}</div>}
          </div>

          <div style={{ textAlign: 'center', margin: '20px 0' }}>
            <p>또는</p>
          </div>

          {/* 이메일 인증 섹션 */}
          <div className="verification-method-box">
            <h4>이메일 인증</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
              <input
                type="email"
                name="email"
                placeholder="이메일 주소"
                value={form.email}
                onChange={handleChange}
                disabled={isEmailVerified || verificationMethod === 'phone'} // 다른 인증 방식 선택 시 비활성화
              />
              <button
                type="button"
                onClick={() => { setVerificationMethod('email'); handleSendEmailCode(); }}
                disabled={isEmailVerified || verificationMethod === 'phone'}
              >
                인증번호 발송
              </button>
            </div>
            {verificationMethod === 'email' && sentEmailCode && !isEmailVerified && (
              <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginTop: 4 }}>
                <input
                  type="text"
                  placeholder="인증번호 입력"
                  value={emailCode}
                  onChange={e => setEmailCode(e.target.value)}
                  style={{ flex: 1 }}
                />
                <button type="button" onClick={handleVerifyEmail}>인증확인</button>
              </div>
            )}
            {isEmailVerified && <span style={{ color: 'green', fontSize: 13 }}>이메일 인증 완료</span>}
            {errors.email && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.email}</div>}
          </div>

          <button
            className="terms-next-btn"
            onClick={() => { /* 이 버튼은 isAnyMethodVerified가 true일 때 자동으로 다음 단계로 넘어감 */ }}
            disabled={!isAnyMethodVerified}
            style={{ marginTop: '20px' }}
          >
            본인인증 완료
          </button>
          {!isAnyMethodVerified && <div style={{ color: 'red', fontSize: 13, marginTop: 8 }}>휴대폰 또는 이메일 중 하나를 인증해야 다음 단계로 진행할 수 있습니다.</div>}
        </div>
      )}

      {currentStep === 'signup' && (
        <form className="auth-form" onSubmit={handleSubmitSignup}>
          <h3>회원 정보 입력</h3>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
            <div style={{ flex: 1 }}>
              <label htmlFor="userId" className="form-label">아이디</label>
              <input
                type="text"
                id="userId"
                name="userId"
                placeholder="아이디"
                value={form.userId}
                onChange={handleChange}
              />
              <button type="button" onClick={checkDuplicateId} style={{ marginLeft: 8 }}>중복확인</button>
            </div>
            {idCheckResult === 'OK' && <span style={{ color: 'green', marginLeft: 8 }}>사용가능</span>}
            {idCheckResult === 'DUPLICATE' && <span style={{ color: 'red', marginLeft: 8 }}>중복</span>}
          </div>
          {errors.userId && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.userId}</div>}

          <label htmlFor="password" className="form-label">비밀번호</label>
          <input
            type="password"
            id="password"
            name="password"
            placeholder="비밀번호"
            value={form.password}
            onChange={handleChange}
          />
          {errors.password && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.password}</div>}

          <label htmlFor="passwordConfirm" className="form-label">비밀번호 확인</label>
          <input
            type="password"
            id="passwordConfirm"
            name="passwordConfirm"
            placeholder="비밀번호 확인"
            value={form.passwordConfirm}
            onChange={handleChange}
          />
          {errors.passwordConfirm && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.passwordConfirm}</div>}

          {/* 본인인증이 완료되었음을 표시 (선택 사항) */}
          {isPhoneVerified && <div style={{ color: 'blue', fontSize: 14, marginBottom: 15 }}>휴대폰 본인인증이 완료되었습니다.</div>}
          {isEmailVerified && <div style={{ color: 'blue', fontSize: 14, marginBottom: 15 }}>이메일 본인인증이 완료되었습니다.</div>}
          
          {errors.general && <div style={{ color: 'red', fontSize: 13, marginBottom: 8 }}>{errors.general}</div>}

          <button type="submit">회원가입</button>
        </form>
      )}
    </div>
  );
}

export default SignupPage;
