import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import './App.css';
import ReservationCalendar from './components/ReservationCalendar';
import TimeSlotSelector from './components/TimeSlotSelector';
import ReservationForm from './components/ReservationForm';
import SideMenu from './components/SideMenu';
import CourseSchedule from './components/admin/CourseSchedule';
import ReservationManagement from './components/admin/ReservationManagement';

function App() {
  const [courses, setCourses] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setSelectedTime(null);
  };

  const handleTimeSelect = (time) => {
    setSelectedTime(time);
  };

  const handleReservationSubmit = (reservationData) => {
    const newReservation = {
      ...reservationData,
      id: Date.now(),
      status: '확인대기',
      date: selectedDate.toISOString().split('T')[0],
      time: selectedTime
    };
    setReservations(prev => [...prev, newReservation]);
    alert('예약이 완료되었습니다!');
    setSelectedDate(null);
    setSelectedTime(null);
  };

  const handleCourseAdd = (newCourse) => {
    setCourses(prev => [...prev, newCourse]);
  };

  const handleCourseDelete = (courseId) => {
    setCourses(prev => prev.filter(course => course.id !== courseId));
  };

  const handleReservationStatusChange = (reservationId, newStatus) => {
    setReservations(prev =>
      prev.map(reservation =>
        reservation.id === reservationId
          ? { ...reservation, status: newStatus }
          : reservation
      )
    );
  };

  const handleReservationDelete = (reservationId) => {
    setReservations(prev =>
      prev.filter(reservation => reservation.id !== reservationId)
    );
  };

  const toggleAdmin = () => {
    setIsAdmin(!isAdmin);
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>노바문화센터</h1>
          <button onClick={toggleAdmin} className="admin-toggle">
            {isAdmin ? '일반 모드' : '관리자 모드'}
          </button>
        </header>

        <div className="main-layout">
          <SideMenu isAdmin={isAdmin} />
          <main className="main-content">
            <Routes>
              {/* 관리자 라우트 */}
              <Route path="/admin" element={
                isAdmin ? (
                  <Routes>
                    <Route path="courses" element={
                      <CourseSchedule 
                        courses={courses}
                        onCourseAdd={handleCourseAdd}
                        onCourseDelete={handleCourseDelete}
                      />
                    } />
                    <Route path="reservations" element={
                      <ReservationManagement 
                        reservations={reservations}
                        onStatusChange={handleReservationStatusChange}
                        onDelete={handleReservationDelete}
                      />
                    } />
                  </Routes>
                ) : (
                  <Navigate to="/" replace />
                )
              } />

              {/* 일반 사용자 라우트 */}
              <Route path="/" element={
                <div className="home-page">
                  <h2>노바문화센터에 오신 것을 환영합니다</h2>
                  <div className="quick-links">
                    <Link to="/courses" className="quick-link">
                      강좌 목록 보기
                    </Link>
                    <Link to="/reservation" className="quick-link">
                      수강 신청하기
                    </Link>
                    <Link to="/my-reservations" className="quick-link">
                      내 예약 확인
                    </Link>
                  </div>
                </div>
              } />

              <Route path="/courses" element={
                <div className="courses-page">
                  <h2>강좌 목록</h2>
                  <div className="courses-grid">
                    {courses.map(course => (
                      <div key={course.id} className="course-card">
                        <h3>{course.name}</h3>
                        <p>강사: {course.instructor}</p>
                        <p>시간: {course.dayOfWeek}요일 {course.startTime} - {course.endTime}</p>
                        <p>최대 수강인원: {course.maxStudents}명</p>
                        <p>{course.description}</p>
                        <Link to="/reservation" className="enroll-button">
                          수강 신청하기
                        </Link>
                      </div>
                    ))}
                  </div>
                </div>
              } />

              <Route path="/reservation" element={
                <div className="reservation-page">
                  <div className="reservation-container">
                    <ReservationCalendar onDateSelect={handleDateSelect} />
                    {selectedDate && (
                      <TimeSlotSelector 
                        selectedDate={selectedDate}
                        onTimeSelect={handleTimeSelect}
                        courses={courses}
                      />
                    )}
                    {selectedDate && selectedTime && (
                      <ReservationForm 
                        selectedDate={selectedDate}
                        selectedTime={selectedTime}
                        onSubmit={handleReservationSubmit}
                        courses={courses}
                      />
                    )}
                  </div>
                </div>
              } />

              <Route path="/my-reservations" element={
                <div className="my-reservations-page">
                  <h2>내 예약 목록</h2>
                  <div className="my-reservations-list">
                    {reservations.map(reservation => (
                      <div key={reservation.id} className="reservation-card">
                        <h3>{reservation.courseName}</h3>
                        <p>강사: {reservation.courseInstructor}</p>
                        <p>날짜: {reservation.date}</p>
                        <p>시간: {reservation.time}</p>
                        <p>상태: <span className={`status ${reservation.status}`}>{reservation.status}</span></p>
                        <button 
                          onClick={() => handleReservationDelete(reservation.id)}
                          className="cancel-button"
                        >
                          예약 취소
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              } />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
