import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaFacebookF, FaInstagram, FaYoutube, FaBloggerB, FaShareAlt, FaSearch } from 'react-icons/fa';
import { FiLogIn } from 'react-icons/fi';
import '../styles.css';
import { useState } from 'react';

function Header() {
  const location = useLocation();
  const [snsOpen, setSnsOpen] = useState(false);
  return (
    <header className="main-header">
      <div className="header-content">
        {/* 기존 로고/메뉴 구조 그대로 */}
        <div className="header-left">
          <Link to="/" className="main-logo">노바문화센터</Link>
          <nav className="header-nav">
            <ul className="header-menu">
              <li><Link to="/programs" className={location.pathname.startsWith('/programs') ? 'active' : ''}>프로그램</Link></li>
              <li><Link to="/instructors" className={location.pathname.startsWith('/instructors') ? 'active' : ''}>강사</Link></li>
              <li><Link to="/news" className={location.pathname.startsWith('/news') ? 'active' : ''}>소식</Link></li>
            </ul>
          </nav>
        </div>
        <div className="header-right-group">
          <div className="header-search">
            <FaSearch className="header-search-icon" />
            <input type="text" placeholder="다양한 클래스를 검색해보세요!" />
          </div>
          <div
            className="header-sns-dropdown"
            onMouseEnter={() => setSnsOpen(true)}
            onMouseLeave={() => setSnsOpen(false)}
          >
            <button className="header-sns-btn" aria-label="SNS">
              <FaShareAlt size={20} />
            </button>
            {snsOpen && (
              <div className="header-sns-list">
                <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" aria-label="Facebook"><FaFacebookF /></a>
                <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" aria-label="Instagram"><FaInstagram /></a>
                <a href="https://youtube.com" target="_blank" rel="noopener noreferrer" aria-label="YouTube"><FaYoutube /></a>
                <a href="https://blog.naver.com" target="_blank" rel="noopener noreferrer" aria-label="Blogger"><FaBloggerB /></a>
              </div>
            )}
          </div>
          <Link to="/login" className="header-login-btn" aria-label="로그인">
            <FiLogIn size={22} />
          </Link>
        </div>
      </div>
    </header>
  );
}

export default Header;
