import React from 'react';
import '../user/styles.css';
import { useParams, useNavigate } from 'react-router-dom';

// 임시 데이터. 실제로는 props나 context, 혹은 API로 받아올 수 있음
const programs = [
  {
    id: 1,
    image: '/main_1.jpg',
    status: '접수중',
    branch: '울산점',
    title: '성장점 자극 베이비 마사지, 2~8개월',
    semester: '여름학기',
    teacher: '김민서',
    schedule: '세부 일정 선택',
    price: '60,000원',
    description: '성장점 자극 베이비 마사지 상세 설명입니다.'
  },
  // ... 나머지 프로그램 데이터 ...
];

function ProgramDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const program = programs.find(p => p.id === Number(id));

  if (!program) return <div>프로그램을 찾을 수 없습니다.</div>;

  return (
    <div className="page-container">
      <button onClick={() => navigate(-1)} className="back-btn">← 목록으로</button>
      <div className="program-detail-card">
        <img src={program.image} alt={program.title} className="program-img" />
        <div className="program-info">
          <div className="program-badges">
            <span className={`badge status ${program.status === '접수중' ? 'open' : 'wait'}`}>{program.status}</span>
            <span className="badge branch">{program.branch}</span>
          </div>
          <div className="program-title">{program.title}</div>
          <div className="program-meta">
            <span>{program.semester} | {program.teacher}</span>
          </div>
          <div className="program-schedule">{program.schedule}</div>
          <div className="program-price">{program.price}</div>
          <div className="program-description">{program.description || '상세 설명이 없습니다.'}</div>
        </div>
      </div>
    </div>
  );
}

export default ProgramDetailPage;
